queries.queryString:
account={{account}} region={{region}} namespace={{namespace}}
| parse "* * * * * * * * * * * * \"*\" \"*\" * * * \"*\"" as Type, DateTime, loadbalancer, Client, Target, RequestProcessingTime, TargetProcessingTime, ResponseProcessingTime, ElbStatusCode, TargetStatusCode, ReceivedBytes, SentBytes, Request, UserAgent, SslCipher, SslProtocol, TargetGroupArn, TraceId
| where tolowercase(loadbalancer) matches tolowercase("{{loadbalancer}}")
| parse field=TargetGroupArn "arn:aws:elasticloadbalancing:*:*:*" as AwsRegion, AccountId, TargetGroup nodrop
| where RequestProcessingTime matches /^[+]?([0-9]+(?:[\.][0-9]*)?|\.[0-9]+)$/ and TargetProcessingTime matches /^[+]?([0-9]+(?:[\.][0-9]*)?|\.[0-9]+)$/ and ResponseProcessingTime matches /^[+]?([0-9]+(?:[\.][0-9]*)?|\.[0-9]+)$/
| (RequestProcessingTime+TargetProcessingTime+ResponseProcessingTime) as ClientLatency
// Need to filter out logs where request_processing_time, target_processing_time, or response_processing_time are -1
// This means the load balancer can't dispatch the request to a target
| avg(ClientLatency) as AverageClientLatency, max(ClientLatency) as MaximumClientLatency by TargetGroup
| order by MaximumClientLatency

